    <script>
        const faqItems = document.querySelectorAll('.faq-item');

        faqItems.forEach(item => {
            const question = item.querySelector('.faq-question');
            const answer = item.nextElementSibling;
            const icon = item.querySelector('i');


            window.addEventListener('load', () => {

                faqItems.forEach(otherItem => {
                    if (otherItem !== item) {
                        const otherAnswer = otherItem.nextElementSibling;
                        const otherIcon = otherItem.querySelector('i');

                        otherAnswer.classList.remove('active');
                        otherIcon.classList.remove('active');
                        otherAnswer.style.maxHeight = "0";
                    }
                });

                answer.classList.toggle('active');
                icon.classList.toggle('active');


                if (answer.classList.contains('active')) {
                    answer.style.maxHeight = answer.scrollHeight + "px";
                } else {
                    answer.style.maxHeight = "0";
                }

            });

            item.addEventListener('click', () => {
                faqItems.forEach(otherItem => {
                    if (otherItem !== item) {
                        const otherAnswer = otherItem.nextElementSibling;
                        const otherIcon = otherItem.querySelector('i');

                        otherAnswer.classList.remove('active');
                        otherIcon.classList.remove('active');
                        otherAnswer.style.maxHeight = "0";
                    }
                });

                answer.classList.toggle('active');
                icon.classList.toggle('active');


                if (answer.classList.contains('active')) {
                    answer.style.maxHeight = answer.scrollHeight + "px";
                } else {
                    answer.style.maxHeight = "0";
                }
            });


        });
    </script>

    <script>
        // Mega Menu Toggle Function
        function toggleMegaMenu() {
            const megaMenu = document.getElementById('megaMenu');
            const megaMenuAdmin = document.getElementById('megaMenuAdmin');
            const megaMenuItem = megaMenu ? megaMenu.closest('.mega-menu-item') : null;
            const megaMenuItemAdmin = megaMenuAdmin ? megaMenuAdmin.closest('.mega-menu-item') : null;
            
            if (megaMenu) {
                megaMenu.classList.toggle('active');
                // Update parent class for arrow rotation
                if (megaMenuItem) {
                    if (megaMenu.classList.contains('active')) {
                        megaMenuItem.classList.add('menu-active');
                    } else {
                        megaMenuItem.classList.remove('menu-active');
                    }
                }
            }
            // Close other mega menu if open
            if (megaMenuAdmin && megaMenuAdmin.classList.contains('active')) {
                megaMenuAdmin.classList.remove('active');
                if (megaMenuItemAdmin) {
                    megaMenuItemAdmin.classList.remove('menu-active');
                }
            }
        }

        // Mega Menu Admin Toggle Function
        function toggleMegaMenuAdmin() {
            const megaMenuAdmin = document.getElementById('megaMenuAdmin');
            const megaMenu = document.getElementById('megaMenu');
            const megaMenuItemAdmin = megaMenuAdmin ? megaMenuAdmin.closest('.mega-menu-item') : null;
            const megaMenuItem = megaMenu ? megaMenu.closest('.mega-menu-item') : null;
            
            if (megaMenuAdmin) {
                megaMenuAdmin.classList.toggle('active');
                // Update parent class for arrow rotation
                if (megaMenuItemAdmin) {
                    if (megaMenuAdmin.classList.contains('active')) {
                        megaMenuItemAdmin.classList.add('menu-active');
                    } else {
                        megaMenuItemAdmin.classList.remove('menu-active');
                    }
                }
            }
            // Close other mega menu if open
            if (megaMenu && megaMenu.classList.contains('active')) {
                megaMenu.classList.remove('active');
                if (megaMenuItem) {
                    megaMenuItem.classList.remove('menu-active');
                }
            }
        }

        // Close mega menu when clicking outside
        document.addEventListener('click', function(event) {
            const megaMenu = document.getElementById('megaMenu');
            const megaMenuAdmin = document.getElementById('megaMenuAdmin');
            const megaMenuItems = document.querySelectorAll('.mega-menu-item');
            
            let clickedInside = false;
            megaMenuItems.forEach(item => {
                if (item.contains(event.target)) {
                    clickedInside = true;
                }
            });
            
            if (!clickedInside) {
                if (megaMenu) {
                    megaMenu.classList.remove('active');
                    const megaMenuItem = megaMenu.closest('.mega-menu-item');
                    if (megaMenuItem) {
                        megaMenuItem.classList.remove('menu-active');
                    }
                }
                if (megaMenuAdmin) {
                    megaMenuAdmin.classList.remove('active');
                    const megaMenuItemAdmin = megaMenuAdmin.closest('.mega-menu-item');
                    if (megaMenuItemAdmin) {
                        megaMenuItemAdmin.classList.remove('menu-active');
                    }
                }
            }
        });

        // Close mega menu when pressing Escape key
        document.addEventListener('keydown', function(event) {
            if (event.key === 'Escape') {
                const megaMenu = document.getElementById('megaMenu');
                const megaMenuAdmin = document.getElementById('megaMenuAdmin');
                if (megaMenu) {
                    megaMenu.classList.remove('active');
                    const megaMenuItem = megaMenu.closest('.mega-menu-item');
                    if (megaMenuItem) {
                        megaMenuItem.classList.remove('menu-active');
                    }
                }
                if (megaMenuAdmin) {
                    megaMenuAdmin.classList.remove('active');
                    const megaMenuItemAdmin = megaMenuAdmin.closest('.mega-menu-item');
                    if (megaMenuItemAdmin) {
                        megaMenuItemAdmin.classList.remove('menu-active');
                    }
                }
            }
        });
    </script>

